import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Container,
  styled,
  Typography,
} from '@mui/material'
import  { useState } from 'react';
import {  Modal, Backdrop, Fade, Paper, TextField, MenuItem } from '@mui/material';

import Grid from '@mui/material/Grid'
import React from 'react'
import { Link, useLocation, useParams } from 'react-router-dom'
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import abi from 'abi/marketplace.json'
import { ethers } from 'ethers';
const DatailsBody = styled(Box)(
  () => `

    margin: 3% 10%;
}
  `,
)
const ImagBody = styled(Box)(
  () => `

    display: flex;
    justify-content: flex-start;
}
  `,
)

const BackBtn = styled(Box)(
  () => `
   margin: 3% 10%;
    display: flex;
    justify-content: flex-start;
    cursor:pointer

}
  `,
)

function NftDetails() {
  const [popupOpen, setPopupOpen] = useState(false);
const [offerAmount, setOfferAmount] = useState('');
const [offerTime, setOfferTime] = useState('1 day');
const handlePopupOpen = () => {
  setPopupOpen(true);
};
cons 
const handlePopupClose = () => {
  setPopupOpen(false);
};
<Modal
  open={popupOpen}
  onClose={handlePopupClose}
  closeAfterTransition
  BackdropComponent={Backdrop}
  BackdropProps={{
    timeout: 500,
  }}
>
  <Fade in={popupOpen}>
    <Paper sx={{ p: 2, width: 400, backgroundColor: 'white' }}>
      {/* NFT details */}
      {/* ... (display NFT details here) */}

      {/* Amount field */}
      <TextField
        fullWidth
        label="Offer Amount"
        variant="outlined"
        value={offerAmount}
        onChange={(e) => setOfferAmount(e.target.value)}
        sx={{ mt: 2 }}
      />

      {/* Time options */}
      <TextField
        select
        fullWidth
        label="Offer Time"
        variant="outlined"
        value={offerTime}
        onChange={(e) => setOfferTime(e.target.value)}
        sx={{ mt: 2 }}
      >
        <MenuItem value="1 day">1 day</MenuItem>
        <MenuItem value="1 week">1 week</MenuItem>
        <MenuItem value="1 month">1 month</MenuItem>
      </TextField>

      {/* Submit button */}
      <Button
        sx={{ mt: 2, background: '#121212' }}
        variant="contained"
        onClick={handleOfferSubmit} // Implement this function to handle offer submission
      >
        Submit Offer
      </Button>
    </Paper>
  </Fade>
</Modal>

  const location = useLocation()
  const data = location.state.d
  const tokenId= parseInt(data.tokenId._hex)

async function handleBuy() {
  try {
    const address = '0xf43E9272E7c505d658cAC2c07Ee551f3dDF18Ac0';
    const contractabi = abi.abi;
    const { ethereum } = window;
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const contractss = new ethers.Contract(address, contractabi,signer);
    await contractss.buy(tokenId,{value:1});

  } catch (error) {
    console.error('Error buying NFTs:', error);
  }
   return (
     <div>{console.log("buy button call")}</div>
   )
 }

 return (
    <>
      <div className="gamfi-breadcrumbs-section">
        <div className="container">
          <div className="apply-heading text-center">
            <h2 className="mb-0">Owned by : -  {data.name}</h2>
          </div>
        </div>
      </div>

      <Link to="/">
      <BackBtn><ArrowBackIcon/>  Back</BackBtn>
      </Link>

      <DatailsBody>
        <Grid container spacing={2}>
          <Grid item xs={2} md={6}>
            <div>

              <ImagBody>
                <img
                  src={`https://ipfs.io/ipfs/Qmb4aNkjZ9XAkWwFndpBYWfdmHr5vRHYkNahH5R3fdQR2a`}
                  width="70%"
                />
              </ImagBody>
            </div>
          </Grid>
          <Grid item xs={10} md={6}>
            <Card sx={{ width: '100%' }}>
              <CardContent>

                <Typography
                  gutterBottom
                  variant="h5"
                  component="div"
                  style={{ display: 'flex', justifyContent: 'space-between' }}
                >
                  <div>
                    Owned by <b>{data.name}</b>
                  </div>
                  <div>
                    Current Price <b>{data.price} ETH</b>
                  </div>
                </Typography>

              <Typography  style={{color:"black"}}>NFT Name : - <b>{data.itemname}</b></Typography>

                <Typography>
                  <div>
                    <Grid item xs={4} sx={{ mt: 5 }}>
                    <Typography variant="h5" component="h5" style={{color:"black"}}>Description : -</Typography>
                    </Grid>
                    <Grid item xs={11}>
                      <p>
