import React, { useState } from 'react';
import Web3 from 'web3';
import { Card, CardMedia, CardContent, Typography } from '@mui/material';

const Profile = () => {
  const [nfts, setNFTs] = useState([]);

  const connectToMetamask = async () => {
    // Check if Metamask is installed and request account access
    if (typeof window.ethereum !== 'undefined') {
      try {
        await window.ethereum.enable();
        const web3 = new Web3(window.ethereum);
        const accounts = await web3.eth.getAccounts();
        const address = accounts[0];
        fetchNFTs(address);
      } catch (error) {
        console.error('Error connecting to Metamask:', error);
      }
    } else {
      console.error('Metamask is not installed');
    }
  };

  const fetchNFTs = async (address) => {
    try {
      // Replace with your contract address and ABI
      const address = '0x2f0DF039B5A139804F2f8b04D724A3fEE21253C2';
      const contractabi = abi.abi;
      const { ethereum } = window;
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const contractss = new ethers.Contract(address, contractabi,signer);
      log
      // Call the getOwnedNFTs function in the smart contract
      const tokenURIs = await contractss.methods.getOwnedNFTs(address).call();

      // Set the retrieved NFTs in the component state
      setNFTs(tokenURIs);
    } catch (error) {
      console.error('Error fetching NFTs:', error);
    }
  };

  return (
    <div>
      <button onClick={connectToMetamask}>Connect to Metamask</button>
      <div>
        {nfts.map((tokenURI, index) => (
          <Card key={index}>
            {/* Display NFT image */}
            <CardMedia component="img" src={tokenURI} alt={`NFT ${index}`} />

            <CardContent>
              {/* Display NFT details */}
              <Typography variant="h5" component="div">
                NFT Title
              </Typography>
              <Typography variant="body2" color="text.secondary">
                NFT Description
              </Typography>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Profile;
