// // import React from 'react'
// import abi from 'abi/ERC721Contract.json'
// import { ethers } from 'ethers';
import React, { useState ,useEffect} from 'react';
import Web3 from 'web3';
import { Card, CardMedia, CardContent, Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import marketplaceabi from 'abi/marketplace.json'
import { nftcontract, marketplaceContract } from 'web3config/web3config';
const Profile = () => {
  const [nfts, setNFTs] = useState([]);

  useEffect(()=>{
    const  apiData = async() => {
      const fetchNFTs = async (Metamaskaddress) => {
         try {
        //   const marketplaceaddress='0xf43E9272E7c505d658cAC2c07Ee551f3dDF18Ac0';
        //   const address = '0x5C28F4E71005CedB42263C26A785B50DbAa51b73';
        //   const contractabi = abi.abi;
        //   const { ethereum } = window;
        //   const provider = new ethers.providers.Web3Provider(ethereum);
        //   const signer = provider.getSigner();
        //   const contractss = new ethers.Contract(address, contractabi,signer);
        //   const marketplacecontractabi = marketplaceabi.abi;
        //   const marketplaceContract=new ethers.Contract(marketplaceaddress, marketplacecontractabi,signer);
        //  console.log(contractss,"contract object");
          const tokenURIs = await nftcontract.getOwnedNFTs(Metamaskaddress );
          const getSuccessfullyBoughtTokens= await marketplaceContract.getSuccessfullyBoughtTokens(Metamaskaddress);
          const tokenUriOfBoughtToken=[];
          if(getSuccessfullyBoughtTokens.length!==0){
            for(let i=0;i<getSuccessfullyBoughtTokens.length;i++){
             tokenUriOfBoughtToken[i]= await nftcontract.tokenURI(getSuccessfullyBoughtTokens[i])
            }
          }
          // const combinedTokenURIs = [...tokenURIs, ...tokenUriOfBoughtToken];
          const combinedTokenURIsSet = new Set([...tokenURIs, ...tokenUriOfBoughtToken]);

          console.log(tokenURIs,"tokenurissssssssssssssssssssssss");  
          // if (combinedTokenURIs !== null) {
          //   // Filter out empty strings from tokenURIs array
          //  // const filteredTokenURIs = tokenURIs.filter(uri => uri !== '');
          //  const filteredTokenURIs = combinedTokenURIs.filter(uri => uri !== '');
          //   if (filteredTokenURIs.length === 0) {
          //     console.log("All tokenURIs are empty strings, skipping setNFTs()");
          //   } else {
          //     console.log(filteredTokenURIs, "tokenuri from profile");
          //     setNFTs(filteredTokenURIs);
          //   }
          // }
          if (combinedTokenURIsSet.size === 0) {
            console.log("All tokenURIs are empty, skipping setNFTs()");
          } else {
            const filteredTokenURIs = Array.from(combinedTokenURIsSet);
            console.log(filteredTokenURIs, "tokenuri from profile");
            setNFTs(filteredTokenURIs);
          }

        } catch (error) {
          console.error('Error fetching NFTs:', error);
        }
      };

      if (typeof window.ethereum !== 'undefined') {
              try {
                await window.ethereum.enable();
                const web3 = new Web3(window.ethereum);
                const accounts = await web3.eth.getAccounts();
                const address = accounts[0];
                console.log(address,"metamask address");
                fetchNFTs(address);
              } catch (error) {
                console.error('Error connecting to Metamask:', error);
              }
            } else {
              console.error('Metamask is not installed');
            }
    };
    apiData()
  },[])

  return (
    <div>
      {/* <button onClick={connectToMetamask}>Connect to Metamask</button> */}

        {nfts.map((tokenURI, index) => (
          <Card key={index}>
            {/* Display NFT image */}
            <CardMedia component="img" src={tokenURI.image} alt={`NFT ${tokenURI}`} />
{/* {console.log(tokenURI.image)} */}
            <CardContent>
              {/* Display NFT details */}
              <Typography variant="h5" component="div">
                NFT Title
              </Typography>
              <Typography variant="body2" color="text.secondary">
              NFT Description
              </Typography>
              <Link to='/listnftforsale'>
              <button >List NFT</button>
              </Link>
            </CardContent>
          </Card>
        ))}
      </div> 
  );
};

export default Profile;
