import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './NFTListingForm.css'; // Create this CSS file and add custom styles there

const NFTListingForm = () => {
  const [selectedTime, setSelectedTime] = useState(null);
  const [customTime, setCustomTime] = useState(null);

  const handleTimeChange = (time) => {
    setSelectedTime(time);
    // If you want to reset the customTime field whenever a pre-defined time is selected
    setCustomTime(null);
  };

  const handleCustomTimeChange = (event) => {
    setCustomTime(event.target.value);
    // If you want to reset the selectedTime whenever a custom time is entered
    setSelectedTime(null);
  };

  return (
    <div className="nft-listing-form">
      <h2>NFT Listing Form</h2>
      <div className="time-selection">
        <p>Select time:</p>
        <label>
          <input
            type="radio"
            value="1 hour"
            checked={selectedTime === '1 hour'}
            onChange={() => handleTimeChange('1 hour')}
          />
          1 Hour
        </label>
        <label>
          <input
            type="radio"
            value="1 day"
            checked={selectedTime === '1 day'}
            onChange={() => handleTimeChange('1 day')}
          />
          1 Day
        </label>
        <label>
          <input
            type="radio"
            value="1 week"
            checked={selectedTime === '1 week'}
            onChange={() => handleTimeChange('1 week')}
          />
          1 Week
        </label>
        <label>
          <input
            type="radio"
            value="custom"
            checked={selectedTime === null && customTime === null}
            onChange={() => handleTimeChange(null)}
          />
          Custom:
          <input
            type="text"
            value={customTime || ''}
            onChange={handleCustomTimeChange}
            placeholder="Enter custom time"
          />
        </label>
      </div>
      {/* Add other input fields for NFT details */}
      {/* e.g., NFT name, description, price, etc. */}
    </div>
  );
};

export default NFTListingForm;
